import React, { useState, useEffect, useRef } from 'react';
import { Container, Typography, TextField, Button, Paper, List, ListItem, ListItemText } from '@/components/ui';
import { Send, Volume2 } from 'lucide-react';

const API_URL = 'http://localhost:3000/api';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    addBotMessage("Merhba! Ana l'assistant dyalk l'Darija Medical Terms. Kifach n9der n3awnek lyum?");
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const addBotMessage = (text, terms = null) => {
    setMessages(prevMessages => [...prevMessages, { text, isBot: true, terms }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (input.trim() === '') return;

    setMessages(prevMessages => [...prevMessages, { text: input, isBot: false }]);
    setInput('');

    try {
      const response = await fetch(`${API_URL}/terms/search`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: input }),
      });
      const data = await response.json();

      if (data.terms && data.terms.length > 0) {
        addBotMessage("Ha shnw l9it:", data.terms);
      } else {
        addBotMessage(data.message || "Ma l9it hta term. Jreb tkteb shi haja khra.");
      }
    } catch (error) {
      console.error('Error processing query:', error);
      addBotMessage("Smeh liya, 3ndi moshkil bash njawbek daba. 3awd jreb mn be3d.");
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const speak = (text, lang) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = lang;
      window.speechSynthesis.speak(utterance);
    } else {
      console.error('Text-to-speech not supported in this browser');
    }
  };

  return (
    <Container>
      <Typography variant="h4" className="my-5">
        Darija Medical Terms Chatbot
      </Typography>
      <Paper className="h-[60vh] overflow-auto p-5 mb-5">
        <List>
          {messages.map((message, index) => (
            <ListItem key={index} className={`flex ${message.isBot ? 'justify-start' : 'justify-end'}`}>
              <Paper elevation={3} className={`p-3 max-w-[70%] ${message.isBot ? 'bg-gray-100' : 'bg-blue-100'}`}>
                <ListItemText primary={message.text} className="whitespace-pre-wrap" />
                {message.terms && (
                  <div className="mt-4">
                    {message.terms.map((term, termIndex) => (
                      <div key={termIndex} className="mb-4 p-4 bg-white rounded shadow">
                        <Typography variant="h6">{term.darija}</Typography>
                        <Typography variant="body2">Category: {term.category_english} ({term.category_darija})</Typography>
                        <Typography variant="body2">English: {term.explanation_english}</Typography>
                        <Typography variant="body2" className="mt-2">Description (Darija): {term.detailed_description_darija}</Typography>
                        <div className="mt-2">
                          <Typography variant="body2">French: {term.french}</Typography>
                          <Typography variant="body2">Spanish: {term.spanish}</Typography>
                          <Typography variant="body2">Italian: {term.italian}</Typography>
                          <Typography variant="body2">German: {term.german}</Typography>
                          <Typography variant="body2">Dutch: {term.dutch}</Typography>
                        </div>
                        <div className="mt-2">
                          <Button onClick={() => speak(term.darija, 'ar-MA')} size="small">
                            <Volume2 className="w-4 h-4 mr-1" /> Darija
                          </Button>
                          <Button onClick={() => speak(term.explanation_english, 'en-US')} size="small">
                            <Volume2 className="w-4 h-4 mr-1" /> English
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </Paper>
            </ListItem>
          ))}
          <div ref={messagesEndRef} />
        </List>
      </Paper>
      <form onSubmit={handleSubmit} className="flex">
        <TextField
          fullWidth
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Kteb message dyalek hna..."
          className="mr-2"
        />
        <Button type="submit" variant="contained">
          <Send className="w-4 h-4 mr-2" />
          Sift
        </Button>
      </form>
    </Container>
  );
}

export default App;
